const loadingImg = "data:image/gif;base64,";

export default {

    preload(el, binding) {
        if (binding.modifiers.bg) {
            el.style.backgroundImage = `url(${loadingImg})`;
            el.style.backgroundRepeat = "no-repeat";
            el.style.backgroundPosition = "center center";
            el.style.backgroundSize = "auto";
        } else {
            el.style.objectFit = "none";
            el.style.objectPosition = "center";
            // el.src = "./img/5-121204193935-5112312321.gif";
            // 图片的数字化   ==>  转换为 base64 文本
            el.src = loadingImg;
        }

        let tempImg = new Image();
        tempImg.src = binding.value;
        // console.log(binding);
        tempImg.onload = function () {
            if (binding.modifiers.bg) {
                el.style.backgroundImage = `url(${binding.value})`;
                el.style.backgroundSize = binding.arg;
            } else {
                el.src = binding.value;
                // el.style.objectFit = "cover"; //?
                el.style.objectFit = binding.arg;
            }
        }
    }
}